INSERT INTO Genres(name, description) VALUES('Drama', 'Focused on emotions and defined by conflict, often looking to reality rather than sensationalism. ');
INSERT INTO Genres(genreid, name, description) VALUES(1, 'Fantasy', 'Films defined by situations that transcend natural laws and/or by settings inside a fictional universe, with narratives that are often inspired by or involve human myths. The genre typically incorporates non-scientific concepts such as magic, mythical creatures, and supernatural elements.');
INSERT INTO Directors(name, bio, birthyear) VALUES('Peter Jackson', 'Sir Peter Jackson made history with The Lord of the Rings trilogy, becoming the first person to direct three major feature films simultaneously. The Fellowship of the Ring, The Two Towers and The Return of the King were nominated for and collected a slew of awards from around the globe, with The Return of the King receiving his most impressive collection of awards. This included three Academy AwardsÂ® (Best Adapted Screenplay, Best Director and Best Picture), two Golden Globes (Best Director and Best Motion Picture-Drama), three BAFTAs (Best Adapted Screenplay, Best Film and Viewers'' Choice), a Directors Guild Award, a Producers Guild Award and a New York Film Critics Circle Award.', 'October 31, 1961');
INSERT INTO Directors(directorid, name, bio, birthyear) VALUES(1, 'Edgar Wright', 'Edgar Howard Wright (born 18 April 1974) is an English director, screenwriter, producer, and actor. He is best known for his comedic Three Flavours Cornetto film trilogy consisting of Shaun of the Dead (2004), Hot Fuzz (2007), and The World''s End (2013), made with recurrent collaborators Simon Pegg, Nira Park and Nick Frost. He also collaborated with them as the director of the television series Spaced.', 'April 18, 1974');
INSERT INTO Movies (title, description, genreid, directorid, imageurl, featured) VALUES('Scott Pilgrim vs. the World', 'In a magically realistic version of Toronto, a young man must defeat his new girlfriend''s seven evil exes one by one in order to win her heart.', 1, 1, 'scottpilgrim.png', True);